// Pentagon numbers
// Problem 44
//
// Pentagonal numbers are generated by the formula, Pn=n(3n−1)/2.
// The first ten pentagonal numbers are:
//
// 1, 5, 12, 22, 35, 51, 70, 92, 117, 145, ...
//
// It can be seen that P4 + P7 = 22 + 70 = 92 = P8. However, their difference,
// 70 − 22 = 48, is not pentagonal.
//
// Find the pair of pentagonal numbers, Pj and Pk, for which their sum and
// difference are pentagonal and D = |Pk − Pj| is minimised;
// what is the value of D?
//
// Answer:
// 5482660

"use strict";

class PentagonNumbers {

    static isPentagonal(number) {
        let penTest = (Math.sqrt(1 + 24 * number) + 1.0) / 6.0;
        return penTest === Math.floor(penTest);
    }

    static output() {
        let notFound = true;
        let result = 0;
        let i = 1;
        while (notFound) {
            i++;
            let n = i * (3 * i - 1) / 2;

            for (let j = i-1; j > 0; j--) {
                let m = j * (3 * j - 1) / 2;
                if (this.isPentagonal(n - m) && this.isPentagonal(n + m)) {
                    result = n - m;
                    notFound = false;
                    break;
                }
            }
        }
        console.log(result);
    }
}

PentagonNumbers.output();
